% Intersecci√≥n de dos listas
intersection([], _, []).
intersection([H|T], L2, [H|R]) :- member(H, L2), intersection(T, L2, R).
intersection([_|T], L2, R) :- intersection(T, L2, R).

% Regla principal de recomendaci√≥n 
recomendar_destino(Gustos, Mes, Presupuesto, Ciudad, Pais, Explicacion) :-
    destino(Ciudad, Pais, ListaGustos, ListaMeses, _),
    obtener_costo(Ciudad, Pais, Costo),
    Costo =< Presupuesto,
    member(Mes, ListaMeses),
    intersection(Gustos, ListaGustos, GustosCoinciden),
    GustosCoinciden \= [],
    atomic_list_concat(GustosCoinciden, ', ', GustosStr),
    atomic_list_concat(ListaGustos, ', ', CaracteristicasStr),
    format(atom(Explicacion), 'Se recomienda este destino porque coincide en gustos: ~w, es buen mes (~w), el costo (~w USD) esta dentro del presupuesto y sus caracteristicas principales son: ~w.', 
            [GustosStr, Mes, Costo, CaracteristicasStr]).

% Regla para obtener costo (si es 0, usa estimado por pa√≠s)
obtener_costo(_Ciudad, Pais, CostoFinal) :-
    destino(_, Pais, _, _, CostoBase),
    (CostoBase =:= 0 -> 
        costo_estimado_por_pais(Pais, CostoEstimado),
        CostoFinal = CostoEstimado
    ; 
        CostoFinal = CostoBase
    ).

