% Intersección de dos listas
intersection([], _, []).
intersection([H|T], L2, [H|R]) :- member(H, L2), intersection(T, L2, R).
intersection([_|T], L2, R) :- intersection(T, L2, R).

% Regla principal de recomendación
recomendar_destino(Gustos, Mes, Presupuesto, Ciudad, Pais, Explicacion) :-
    destino(Ciudad, Pais, ListaGustos, ListaMeses, Costo),
    Costo =< Presupuesto,
    member(Mes, ListaMeses),
    intersection(Gustos, ListaGustos, GustosCoinciden),
    GustosCoinciden \= [],
    atomic_list_concat(GustosCoinciden, ', ', GustosStr),
    atomic_list_concat(ListaGustos, ', ', CaracteristicasStr),
    ( Costo =:= 0 ->
        CostoStr = 'desconocido'
    ;   number_string(Costo, CostoStr)
    ),
    format(atom(Explicacion), 'Se recomienda este destino porque coincide en gustos: ~w, es buen mes (~w), el costo (~w) esta dentro del presupuesto y sus caracteristicas principales son: ~w.', [GustosStr, Mes, CostoStr, CaracteristicasStr]).
